/**
 * Configuration operations are 8-bit.
 */
typedef Op ConfigOp;

/**
 * Set or update a configuration value.
 */
struct ConfigSet {
    ConfigOp   Op;     /* With the ConfigOp_e cast to a (ConfigOp)
    KeyValue   OpValues<>;
};

/**
 * An array of OpConfigSet values.
 */
struct ServerConfigure {
    uint8_t      AOID;    /* With the APID_e value cast to a (AOID) */
    OpConfigSet  SetValues<>;
};
